import axios from 'axios';
import { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import MeetupList from '../components/meetups/MeetupList';
import { useAuthContext } from '../hooks/useAuthContext';


function AllMeetupsPage() {
  const [isLoading, setIsLoading] = useState(true);
  const [loadedMeetups, setLoadedMeetups] = useState([]);


  const history = useHistory()
  const {user} = useAuthContext()
  if(!user){
    history.replace('/login')
  }

  useEffect(() => {
    setIsLoading(true);


    // const config = {
    //   headers: { 
    //     Authorization: `Bearer eyJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJhbmFzQGdtYWlsLmNvbSIsImlhdCI6MTY3NzE5NTUyNCwiZXhwIjoxNjc3MTk2OTY0fQ.lN0O1VtSLyqXSBeVrm9wtZ83lRbZcZPKMdmbOLkBCTZhQl3nKpFwv9cIqSa9CLD6IYuSIMk_SVu2Lt5m0fPqkw`
    //   }
    // };

    // axios.get( 
    // 'http://localhost:8080/api/v1/meetup',
    // config
    // ).then(console.log)
    // .catch(console.log);

    fetch(
      'http://localhost:8080/api/v1/auth/meetup',{
        method:"GET",
        headers:{headers: {"Authorization": 'Bearer eyJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJhbmFzLmFscWFzc2ltQGdtYWlsLmNvbSIsImlhdCI6MTY3NzE5NDExNCwiZXhwIjoxNjc3MTk1NTU0fQ.CnfcH34SmaHJFJe73Eoi5Q1Al45Ne9vRImcBFTMuwdgThSKB4ks14pTznYp_Zv5bInq9QDolyELV-B5CfrTZhw'}}
      ,
      }
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        const meetups = [];

        for (const key in data) {
          const meetup = {
            id: key,
            ...data[key]
          };

          meetups.push(meetup);
        }


        setLoadedMeetups(meetups);
      });
  }, []);

  if (isLoading) {
    return (
      <section>
        <p>Loading...</p>
      </section>
    );
  }

  return (
    <section>
      <h1>All Meetups</h1>
      <MeetupList meetups={loadedMeetups} />
    </section>
  );
}

export default AllMeetupsPage;
