import axios from 'axios';
import { useState, useEffect } from 'react';
import { Alert } from 'react-bootstrap';
import { useHistory } from 'react-router-dom';
import MeetupList from '../components/meetups/MeetupList';
import { useAuthContext } from '../hooks/useAuthContext';


function AllMeetupsPage() {
  const [isLoading, setIsLoading] = useState(true);
  const [loadedMeetups, setLoadedMeetups] = useState({meetUps:[],error:null});


  const history = useHistory()
  const {user} = useAuthContext()
  if(!user){
    history.replace('/login')
  }


  useEffect(() => {
    setIsLoading(true);


    const config = {
      headers: { 
        Authorization: `Bearer ${user.token}sd`
      }
    };


    axios.get( 
    'http://localhost:8080/api/v1/meetup',
    config
    ).then((response) => {
          const data = response.data.meetUps
          const error = response.data.error
          console.log(response)

          const meetups = [];
          if(data){
          for (const key in data) {
            const meetup = {
              id: key,
              ...data[key]
            };

            meetups.push(meetup);
          }
        }


          setLoadedMeetups(meetups,error);
          setIsLoading(false)
        })
    .catch(console.log);


  }, [user]);

  if (isLoading) {
    return (
      <section>
        <p>Loading...</p>
      </section>
    );
  }

  return (
    <section>
      <h1>All Meetups</h1>
      {loadedMeetups.error && <Alert variant='danger'>{loadedMeetups.error}</Alert>}
      <MeetupList meetups={loadedMeetups.meetUps} />
    </section>
  );
}

export default AllMeetupsPage;
