            // }
            // if(response.ok){

            //     localStorage.setItem("user",JSON.stringify(json))

            //     dispatch({type:'LOGIN',payload:json})

            //     setLoading(false)

            // }
        }catch(e){
            setLoading(false)

        }



    }


    return {signUp,isLoading,error}
}
